// Push notification handler + cache cleanup for the service worker
// This file is imported by the service worker generated by vite-plugin-pwa

// --- Cache versioning & cleanup ---
const CACHE_PREFIX = "doula-care-";
const CACHE_VERSION = "v1.0.1";
const CURRENT_CACHE = CACHE_PREFIX + CACHE_VERSION;

self.addEventListener("activate", (event) => {
  event.waitUntil(
    caches.keys().then((keys) =>
      Promise.all(
        keys
          .filter((key) => key.startsWith(CACHE_PREFIX) && key !== CURRENT_CACHE)
          .map((key) => {
            console.log("[SW] Deleting old cache:", key);
            return caches.delete(key);
          })
      )
    )
  );
});

// Listen for SKIP_WAITING message from the client
self.addEventListener("message", (event) => {
  if (event.data?.type === "SKIP_WAITING") {
    self.skipWaiting();
  }
});

// --- Push notifications ---
self.addEventListener("push", (event) => {
  if (!event.data) return;

  try {
    const data = event.data.json();
    const { title, body, icon, badge, url, tag, priority, require_interaction, type } = data;

    const isHighPriority = priority === "high" || type === "labor_started" || type === "new_contraction";

    const options = {
      body: body || "",
      icon: icon || "/pwa-icon-192.png",
      badge: badge || "/pwa-icon-192.png",
      tag: tag || type || "default",
      renotify: true,
      requireInteraction: require_interaction ?? isHighPriority,
      data: { url: url || "/", type: type || "general" },
      vibrate: isHighPriority ? [300, 100, 300, 100, 300] : [200, 100, 200],
      actions: [
        { action: "open", title: "Abrir" },
        { action: "close", title: "Fechar" },
      ],
    };

    event.waitUntil(
      self.registration.showNotification(title || "Papo de Doula", options)
    );
  } catch (err) {
    console.error("Error showing push notification:", err);
  }
});

self.addEventListener("notificationclick", (event) => {
  event.notification.close();

  if (event.action === "close") return;

  const url = event.notification.data?.url || "/";

  event.waitUntil(
    clients.matchAll({ type: "window", includeUncontrolled: true }).then((clientList) => {
      for (const client of clientList) {
        if (client.url.includes(self.location.origin) && "focus" in client) {
          client.navigate(url);
          return client.focus();
        }
      }
      return clients.openWindow(url);
    })
  );
});
